"use client";
import { useRouter } from "next/navigation";
import React, { useEffect, useState } from "react";
import Image from "next/image";
import Link from "next/link";
import ListingCard from "./ListingCard";
import { listingsApi } from "@/lib/api/listings";
import { Image_URL } from "@/config/constants";
import Breadcrumbs from "@/components/WebsiteComponents/ReuseableComponenets/Breadcrumbs";
import { useTranslation } from "react-i18next";

const filters = [
  { label: "Last 45 days", value: "45d" },
  { label: "Last 7 days", value: "7d" },
  { label: "Last 24 hours", value: "24h" },
  { label: "Last 1 hours", value: "1h" },
];

const listingsData = [
  {
    id: 1,
    title: "Macbook",
    watchers: 0,
    views: 11,
    price: 2000,
    closingDate: "2025-07-10",
    image: "/watch.jpg",
  },
  {
    id: 2,
    title: "iphone",
    watchers: 3,
    views: 28,
    price: 1500,
    closingDate: "2025-07-10",
    image:
      "data:image/webp;base64,", // ðŸ‘ˆ must be in /public
  },
  {
    id: 3,
    title: "Camera",
    watchers: 1,
    views: 9,
    price: 500,
    closingDate: "2025-07-12",
    image: "/shirt.jpg",
  },
];

export default function SellingClient() {
  // const [filter, setFilter] = useState("all");
  const router = useRouter();
  const commonActions = (id) => [
    { label: "Promote listing", href: `/promote/${id}` },
    { label: "Edit listing", href: `/listing/viewlisting/${id}` },
    { label: "See similar", onClick: () => alert("Selling similar...") },
  ];

  const [filter, setFilter] = useState("45d");
  const [allListings, setAllListings] = useState([]);
  const [filteredListings, setFilteredListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedListing, setSelectedListing] = useState(null);

  useEffect(() => {
    fetchListings();
  }, []);

  useEffect(() => {
    applyFilter();
  }, [filter, allListings]);

  const fetchListings = async () => {
    try {
      setLoading(true);
      const response = await listingsApi.getUserListings({});
      const listData = response?.data || [];
      setAllListings(listData);
    } catch (error) {
      console.error("Failed to fetch listings:", error);
    } finally {
      setLoading(false);
    }
  };

  const applyFilter = () => {
    const now = new Date();
    let threshold;

    if (filter === "45d") {
      threshold = new Date(now.setDate(now.getDate() - 45));
    } else if (filter === "7d") {
      threshold = new Date(now.setDate(now.getDate() - 7));
    } else if (filter === "24h") {
      threshold = new Date(now.setHours(now.getHours() - 24));
    } else if (filter === "1h") {
      threshold = new Date(now.setHours(now.getHours() - 1));
    } else {
      setFilteredListings(allListings);
      return;
    }

    const filtered = allListings.filter((listing) => {
      const createdAt = new Date(listing.created_at);
      return createdAt >= threshold;
    });

    setFilteredListings(filtered);
  };

  const items = [
    { label: "Home", href: "/" },
    { label: "Account", href: "/account" },
    { label: "Selling" },
  ];

  return (
    <div className="min-h-screen  text-gray-800">
      <Breadcrumbs
        items={items}
        styles={{
          nav: "flex justify-start text-sm font-medium bg-white border-b border-gray-200 py-2",
        }}
      />

      <h1 className="text-2xl font-bold text-green-600 uppercase mb-1 mt-5">
        {t("Selling")}
      </h1>
      <p className="text-sm mb-4 mt-3">{filteredListings.length} {"- "} {t("Listings")}</p>

      <div className="mb-4">
        <select
          className="border border-green-500 text-green-500 px-4 py-1 rounded-full text-sm hover:bg-green-50"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          {filters.map((item) => (
            <option key={item.value} value={item.value}>
             {t(item.label)}
            </option>
          ))}
        </select>
      </div>

      {/* Listings */}
      {loading ? (
        <p className="text-sm text-gray-500">{t("loadingListings...")}</p>
      ) : filteredListings.length === 0 ? (
        <p className="text-sm text-gray-500">{t("No listings found.")}</p>
      ) : (
        filteredListings.map((listing) => (
          <ListingCard
            key={listing.id}
            listing={{
              id: listing.id,
              title: listing.title,
              price: listing.buy_now_price || "N/A",
              views: listing.view_count || 0,
              watchers: 0,
              offers: listing.selling_offers || [],
              slug: listing.slug,
              closingDate: listing.expire_at,
              image: listing.images?.[0]?.image_path
                ? `${Image_URL}${listing.images[0].image_path}`
                : "/default-image.jpg",
              link: `/marketplace/${listing.category?.slug || "unknown"}/${
                listing.slug
              }`,
            }}
            // actions={commonActions(listing.slug)}
            //             const commonActions = (id) => [
            //   { label: "Promote listing", href: `/promote/${id}` },
            //   { label: "Edit listing", href: `/listing/edit/${id}` },
            //   { label: "See similar", onClick: () => alert("Selling similar...") },
            // ];
            actions={[
              // { label: "Promote listing", href: `/promote/${listing?.id}` },
              { label: "Promote listing" },
              { label: "Edit listing", href: `/listing/viewlisting?slug=${listing?.slug}` },
              {
                label: "See similar",
                onClick: () =>
                  router.push(
                    `/marketplace/${listing.category?.slug || "unknown"}`
                  ),
              },
            ]}
          />
        ))
      )}

      {/* <div className="mt-6 text-center text-sm text-green-600 hover:underline cursor-pointer">
        â†‘ Back to top
      </div> */}
    </div>
  );
}
